<?phpclass Test_runner extends CI_Controller {			function index(){		$this->load->view('spec_runner');	}			function tests(){		$this->load->library('unit_test');		$test = 1 + 1;		$expected_result = 2;		$test_name = 'Adds one plus one';		$this->unit->run($test, $expected_result, $test_name);		echo $this->unit->report();			}}?>