<?phpclass Form_sign_up extends CI_Model{			//global variable declaration			//constructor method	function __construct(){		parent::__construct();			}		function insert_user_detail_into_db(){		$username = $this->input->post('username');		$email = $this->input->post('email');		$password = $this->input->post('passwd2');					$data = array(			'username' =>$username,			'email'=>$email,			'password'=> $password		);			$this->db->insert('users',$data);	}												//sample test function	function test(){		$query = $this->db->query('SELECT email FROM users WHERE id = ?',array(1));					$data = array_shift($query->result_array());		echo ($data['email']);	}					}?>